import { Link } from "react-router-dom";
import { useState } from 'react';
import logo from "../images/logo.png";
import InputPage from './InputPage';
const AppHeader = () => {

    const [menuScreen, showMenuScreen] = useState(false);

    return (
        <div id="header" className="header d-flex vw-100">
            <button onClick={() => showMenuScreen(true)}class="btn menu-button bg-secondary text-white">Menu</button>
            { menuScreen ? <InputPage/>: null}
            <div className="title d-flex mx-auto">
                <img className="image logo" alt="logo" src={logo}></img>
                <h4>Lecture Listener</h4>
            </div>
        </div>
    )
}

export default AppHeader
